INSERT INTO `book` (`bid`, `bname`, `aname`, `bdesc`, `bdate`, `catname`, `download`, `bimg`, `bpdf`) VALUES
	(18, 'الخيميائي', 'باولو كويلو', 'تتحدث الرواية عن راع أندلسي شاب يدعى سانتياغو. مضى للبحث عن حلمه المتمثل بكنزٍ مدفون قرب أهرامات مصر، بدأت رحلته من أسبانيا عندما إلتقى الملك &quot;ملكي صادق&quot; الذي أخبره عن الكنز، عَبَرَ مضيق جبل طارق ماراً بالمغرب حتى بلغ مصر، وكانت توجهه طوال الرحلة إشارات غيبية.\r\n\r\nوفي طريقه للعثور على كنزه الحلم، أحداث كثيرة تقع، كل حدث منها استحال عقبة تكاد تمنعه من متابعة رحلته، إلى أن يجد الوسيلة التي تساعده على تجاوز هذه العقبة', '1988-02-15', 'روايات', 2, 'الخيميائي.jpg', 'رواية الخيميائي.pdf'),
	(19, 'صاحب الظل الطويل', 'جين ويبستر', 'تحكي هذه الرواية مغامرة جودي آبوت التي خرجت من الميتم لان احد الاوصياء قرر التكفل بعملية تعليمها بعد ان لاحظ موهبتها الاستثنائية في الكتابة والكتابة الريفة تحديداً واصرارها على السعادة بصفتها خيارا في الحياة وليست حقا مكتسبا من الولادة', '1912-09-11', 'روايات', 0, 'صاحب الظل.jpg', 'صاحب الظل الطويل.pdf'),
	(20, 'أرض زيكولا', 'عمرو عبد الحميد', 'هل جرّبت أن تتعامل بعملة مختلفة عن العملات الورقية .. ليست معدنية وليست ذهبية ...الثروة هنا من نوع آخر ... لن تدفع مالاً لتأخذ ..بل ستدفع من ذكائك ..ستدفع من وحدات عقلك... ولكن انتبه فـ للثروة حدود ...فهنا المفلس لا يعامل باحتقار أو يُهان كما يحدث في عالمك .. بل يتعرض للموت .. احذر .. فأنت .. في أرض زيكولا', '2010-02-17', 'روايات', 0, 'ارض زيكولا.jpg', 'أرض زيكولا.pdf'),
	(21, 'ليطمئن قلبي', 'أدهم شرقاوي', 'ذهب الرازي يوما الى نيسابور فتراكض له الناس فقالت امرأة عجوز: من هذا؟ فقيل لها: هذا الرازي الذي يعرف ألف دليل على وجود الله\r\n\r\nفقالت: لو لم يكن في قلبه ألف شك ما احتاج الى ألف دليل!\r\nفلما بلغه قولها، قال: اللهم إيمانا كإيمان العجائز!\r\nهذه الرواية مهداة الى المؤمنين بالله إيمان العجائز بلا فلسفة ولا تعقيد..\r\nالذين لو قيل لأحدهم أعطنا دليلا على وجود الله لربما تلعثم ولم تسعفه لغته، ولكن ما يضره وحسبه من الإيمان أن كل خلية في جسمه تؤمن أن: لا إله إلا الله وأن محمداً رسول الله', '2019-05-14', 'روايات', 0, 'ليطمئن قلبي.jpg', 'ليطمئن قلبي.pdf'),
	(22, 'أثر الفراشة', 'محمود درويش', ' صفحات مختارة من يوميات، كُتبت بين صيف 2006 وصيف 2007 يضم الكتاب عشرات النصوص الشعرية التي تنقلنا من حلم إنساني إلى آخر. لكن أحلام محمود درويش غير إنها مثقلة بالواقع وأسيرة القضية.وهي أسيرة الخوف والقلق والحنين والرحيل والجدران..ولا تغادره المرأة بتاتاً', '2007-06-05', 'شعر', 0, 'أثر الفراشة.png', 'أثر الفراشة.pdf'),
	(23, 'ديوان الإمام الشافعي', 'الإمام الشافعي', 'عنى هذا الكتاب بجمع ديوان الإمام الشافعي وتبويبه تبعاً لحروف الهجاء وحركات الإعراب، والذي تناثرت أبياته ومقطعاته في مصارد شتى، أدبية ولغوية وتاريخية… وهذا الشعر يمثل نموذجاً لشاعرية رجل الفقه مجمله مروياً من هذين الينبوعين الدافقين ومن سيرة الراشدين وكبار الصحابة والأئمة\r\nديوان الامام الشافعي', '1900-09-11', 'شعر', 1, 'ديوان الإمام الشافعي.jpg', 'ديوان الشافعي.pdf'),
	(24, 'الشوقيات', 'أحمد شوقي', 'حوى آثارًا شعريةً مختلفةَ الأغراض والأهداف، وضمَّ مجموعةً من القصائد حول الوصف والغَزَل والرِّثاء؛ هذا الأخير الذي نَظَمه شوقي في تأبين زعماء مصر ورجالاتها الذين عاصرهم. كما تضمَّنَت الشوقيات إحياءَ ذكرى الخالدين من أعلام التاريخ بمآثرهم الإنسانية وعبقريَّتهم، وقد أرَّخ فيها شوقي لكثيرٍ من الأحداث والمناسبات، وحكى كثيرًا من الحكايات على ألسنة الطير والحيوانات.', '1926-09-28', 'شعر', 0, 'الشوقيات.jpg', 'الشوقيات.pdf'),
	(25, 'آلة الزمن', 'هربرت جورج ويلز', 'آلة الزمن هي أول رواية خيالية للكاتب هربرت جورج ويلز صدرت سنة 1895، عن عالم انتقل إلى المستقبل البعيد (حوالي 802,701 بعد الميلاد) ووجد كيف أن مستقبل البشرية مظلم، حيث أنه من خلال المسافة الطبقية بين الأغنياء والفقراء، سيظهر جنسين من البشر، كل جنس أحفاد لمن سبقوه فأحفاد الأغنياء سيكونون جنساَ غبياَ ضعيفأ يسمى (الأيلو)', '1895-06-30', 'خيال علمي', 0, 'آلة الزمن.jpg', 'آلة الزمن.pdf'),
	(26, 'كوكب العدم', 'أسامة محمد عارضة', 'الارض تعيش في زمن الصراع التي تنتهي الحضارة من حولها اكثر مما يتخيل الجميع بعضها صديق و بعضها عدو و هي سيدة في مجرتها قوية بين الاقوياء بفضل علومها و قادتها و  تكنولوجيتها  يسيطر على اكثر قليال من نصف إمبراطورتيها جاك القاسي العنيف عدو فراس الذي يتبع منظمة رقم صفر التي تسيطر على اقل بقليل من نصف امبراطورية الارض و الصراع ال ينتهي بينها و  بين من حولها او بين قادتها ', '2023-07-04', 'خيال علمي', 46, 'كوكب العدم.png', 'كوكب العدم.pdf'),
	(27, 'قوة التحكم في الذات', 'إبراهيم الفقي', 'معظم الناس يبرمجون منذ الصغر على أن يتصرفوا معينة ، ويشعر بأحاسيس سلبية من أسباب معينة ، بالتعاسة لأسباب معينة ، واستمروا في حياتهم بنفس التصرفات تماماً مثل الفيل نيلسون .. وأصبحوا سجناء برمجتهم واعتقاداتهم السلبية التي تحد من قدرتهم في الحصول على ما يستحقون في الحياة ، فنجد نسب الطلاق تزداد ارتفاعاً ، والشركات تغلق أبوابها', '2000-06-27', 'تنمية بشرية', 0, 'قوة التحكم في الذات.png', 'قوة التحكم في الذات.pdf'),
	(28, 'العادات الذرية', 'جيمس كلير', 'أيًا كانت أهدافك فإن كتاب &quot;العادات الذرية&quot; يقدم لك إطار عمل من أجل تطوير مهاراتك في كل يوم. جيمس كلير واحد من أبرز الخبراء في مجال اكتساب العادات، يوضح لنا في كتابه هذا استراتيجيات عملية تعلمنا كيف نستطيع اكتساب عادات جيدة، وكيف نتخلص من القديمة السلبية، ويشرح لنا طريقة إدارة وإتقان أفعالنا اليومية البسيطة التي نستطيع من خلالها تحقيق نتائج مذهلة.', '2019-06-11', 'تنمية بشرية', 1, 'العادات الذرية.jpg', 'العادات الذرية.pdf'),
	(29, 'فن اللامبالاة', 'مارك مانسون', 'ظل يُقال لنا طيلة عشرات السنوات إن التفكير الإيجابي هو المفتاح إلى حياة سعيدة ثرية. لكن مارك مانسون يشتم تلك &quot;الإيجابية&quot; ويقول: &quot; فلنكن صادقين، السيء سيء وعلينا أن نتعايش مع هذا &quot;. لا يتهرّب مانسون من الحقائق ولا يغلفها بالسكّر، بل يقولها لنا كما هي: جرعة من الحقيقة الفجِّة الصادقة المنعشة هي ما ينقصنا اليوم.', '2016-09-13', 'تنمية بشرية', 0, 'فن اللامبالاة.jpg', 'فن اللامبالاة.pdf'),
	(30, 'أسباب النزول', 'عبد الرحمن السيوطي جلال الدين', 'يُعتبر هذا الكتاب من أهم ما أُلِّف في الباب، ويتميَّز على كتاب الواحدي رحمه الله بالاختصار، والزيادات عليه، وعزو كل حديثٍ إلى من خرَّجه من أصحاب الكتب المعتبرة، وتمييز الصحيح من غيره، والمقبول من المردود، والجمع بين الروايات المتعددة، وتنحية ما ليس من أسباب النزول.', '1505-10-25', 'علوم القرآن', 0, 'اسباب النزول.jpg', 'أسباب_النزول.pdf'),
	(31, 'بلاغة القرآن في الإعجاز', 'بهجت عبد الواحد الشيخلي', 'يتناول الكتاب معنى كل سورة من القرآن الأربع عشرة بعد المائة، وجزاء الشرح حسب ترتيب الآيات في القرآن، من سورة البسملة إلى آخر سورة الناس، وإعراب آيات كل سورة إعرابًا تفصيليًا وافيًا ، مع تفسيرها وشرح الغامض.', '2001-06-11', 'علوم القرآن', 0, 'بلاغة القرآن.jpg', 'بلاغة_القرآن_الكريم_في_الإعجاز.pdf'),
	(32, 'لغز وادي المساخيط', 'محمود سالم', 'المغامرون الخمسة سلسلة مغامرات بوليسية، صدرت عن دار المعارف في السبعينيات أبطالها تختخ ولوزة ومحب ونوسة الذين رافقوا أجيالا متعاقبة في الوطن العربي، وقد صدر منها عشرات الكتب وقدمت في عدة مسلسلات كرتونية ، عن السلسلة يقول المؤلف أعتقد أن المغامرين الخمسة هي نوع من استعادة شجون وذكريات طفل متوحد ومنعزل.', '1976-06-08', 'قصص أطفال', 0, 'لغز وادي المساخيط.jpg', 'لغز_وادى_المساخيط.pdf'),
	(33, 'لغز عصابة الأشباح', 'محمود سالم', 'إنهم أشباح طوال القامة ، وفى لحظات يتحولون إلى أشباح قصار القامة ، ثم يتلاشون فلا يجدهم أحد .\r\nوقد اكتشف الشاويش " فرقع " أن " تختخ " و " محب " و " عاطف " فى هذه العصابة !\r\nاقرأ اللغز وحاول أن تكشف سر العصابة مع المغامرين الخمسة .', '2023-06-26', 'قصص أطفال', 0, 'عصابة الأشباح.jpg', 'لغز_عصابة_الأشباح.pdf'),
	(34, 'مطبخ منال العالم', 'منال العالم', 'الشيف &quot;منال العالم&quot; سعت لتقدم الفنون لهذه الأم حيث اعتنت بوضع كتاب حمل إسمها وحملت صفحاته ألذ وأطيب الوصفات من المطبخ العربي والغربي، والأكثر شهرة، فكانت هذه التشكيلة من الوصفات المختارة، والوصفات التقليدية، الشعبية، العالمية، العربية والغربية، حيث أتاحت لربة المنزل ولكل هواة الطبخ فرصة لإعداد مائدة متكاملة ترضي كافة الأذواق وهي لم تكتف بتقديم كتابها هذا بل أطلت من خلال البرامج التليفزيونية التي قدمتها على الشاشات العربية المتنوعة', '2016-07-28', 'طبخ وطعام', 0, 'منال.jpg', 'منال العالم.pdf'),
	(35, 'أشهى الحلويات', 'السيدة بو حامد', 'كتاب أشهى الحلويات تأليف السيدة بو حامد يقدم وصفات جديدة و مميزة فى عالم طهى بو حامد فى أشهى الحلويات، موضحة من خلال الكتاب مكونات كل نوع من الحلويات وطريقة عمله، كما تقدم أنواع متعددة من الحلويات الغربية ليست فقط الحلوى العربية و الشرقية.', '2023-01-03', 'طبخ وطعام', 0, 'اشهى الحلوياات.jpg', 'اشهى الحلويات.pdf');

