INSERT INTO `policy` (`id`, `policytext`) VALUES
	(1, 'سياسة الخصوصية لموقع مكتبة سحاب : \r\n                        نحن في مكتبة سحاب نهتم بخصوصية زوارنا ونحرص على حماية معلوماتهم الشخصية. توضح سياسة الخصوصية\r\n                        التالية كيفية جمعنا واستخدامنا للمعلومات الشخصية التي يتم تقديمها لنا عند استخدامك لموقعنا.\r\n                        \r\n                        جمع المعلومات: \r\n                        نقوم بجمع معلومات شخصية محددة عندما تتفاعل معنا على الموقع، مثل عنوان البريد الإلكتروني عندما\r\n                        تشترك في النشرة الإخبارية أو تقدم طلبًا للاشتراك.\r\n                        نستخدم تقنيات مثل ملفات تعريف الارتباط (الكوكيز) لجمع معلومات حول استخدامك للموقع، وتحسين تجربتك\r\n                        وتخصيص المحتوى.\r\n                        \r\n                        استخدام المعلومات:\r\n                        نستخدم المعلومات الشخصية التي نجمعها لتقديم الخدمات والمحتوى المطلوب، وتحسين وتخصيص تجربتك على\r\n                        الموقع.\r\n                        قد نستخدم المعلومات للتواصل معك بخصوص المستجدات والعروض والأخبار المتعلقة بالموقع، وذلك فقط إذا\r\n                        وافقت على ذلك.\r\n                        \r\n                        حماية المعلومات:&gt;\r\n                        نحمي المعلومات الشخصية التي تقدمها لنا بواسطة إجراءات أمنية ملائمة للوقاية من الوصول غير المصرح\r\n                        به أو الاستخدام أو الكشف عنها.\r\n                        نحتفظ بالمعلومات الشخصية فقط للفترة الزمنية اللازمة لتحقيق الأغراض التي جمعت من أجلها، ما لم\r\n                        يُطلب منا الاحتفاظ بها لفترة أطول بموجب القانون.\r\n                        \r\n                        الكشف عن المعلومات:&gt;\r\n                        لا نقوم ببيع أو تأجير أو مشاركة المعلومات الشخصية التي نحصل عليها مع أي جهة خارجية، ما لم يكن\r\n                        لدينا إذن صريح منك أو يكون هناك طلب قانوني للكشف عنها.\r\n                        قد نقوم بمشاركة المعلومات الشخصية مع مقدمي الخدمات الذين يعملون باسمنا لتسهيل عملياتنا وتوفير\r\n                        الخدمات المطلوبة، ولكن يلتزمون بعدم استخدام تلك المعلومات لأي غرض آخر.\r\n                        قد نقوم بالكشف عن المعلومات الشخصية إذا اعتقدنا بحسن نية أن ذلك ضروري للامتثال للقوانين\r\n                        والتنظيمات السارية أو لحماية حقوقنا وسلامتنا أو حقوق المستخدمين الآخرين.\r\n                        \r\n                        حقوق الزائر:\r\n                        لديك الحق في الوصول إلى المعلومات الشخصية التي لدينا عنك وتحديثها أو حذفها إذا كانت غير صحيحة أو\r\n                        غير محدثة.\r\n                        يمكنك ممارسة حقوقك عن طريق الاتصال بنا عبر المعلومات المتاحة في صفحة اتصل بنا.');

